int prim(std::vector<std::vector<std::pair<int, int>>>& graph) {
    std::priority_queue<std::pair<int, int>, std::vector<std::pair<int, int>>, std::greater<>> pq;
    std::vector<bool> inMST(graph.size(), false);
    int mstWeight = 0;
    pq.emplace(0, 0);

    while (!pq.empty()) {
        auto [w, u] = pq.top();
        pq.pop();
        if (inMST[u]) continue;
        inMST[u] = true;
        mstWeight += w;
        for (auto& [v, weight] : graph[u]) {
            if (!inMST[v]) {
                pq.emplace(weight, v);
            }
        }
    }
    return mstWeight;
}

