struct Edge { int u, v, weight; };

int kruskal(std::vector<Edge>& edges, int n) {
    std::sort(edges.begin(), edges.end(), [](Edge& a, Edge& b) { return a.weight < b.weight; });
    std::vector<int> parent(n);
    iota(parent.begin(), parent.end(), 0);

    auto find = [&](int u) {
        while (parent[u] != u) u = parent[u];
        return u;
    };

    int mstWeight = 0;
    for (auto& edge : edges) {
        int pu = find(edge.u), pv = find(edge.v);
        if (pu != pv) {
            parent[pu] = pv;
            mstWeight += edge.weight;
        }
    }
    return mstWeight;
}

topological_sort.cpp_implementation:
std::vector<int> topologicalSort(std::vector<std::vector<int>>& graph) {
    int n = graph.size();
    std::vector<int> inDegree(n, 0), result;
    for (auto& neighbors : graph)
        for (int v : neighbors)
            inDegree[v]++;

    std::queue<int> q;
    for (int i = 0; i < n; i++)
        if (inDegree[i] == 0) q.push(i);

    while (!q.empty()) {
        int u = q.front();
        q.pop();
        result.push_back(u);
        for (int v : graph[u])
            if (--inDegree[v] == 0)
                q.push(v);
    }
    return result;
}

