class HashTable {
    int* table;
    int capacity;
public:
    HashTable(int size) : capacity(size) { table = new int[size] { -1 }; }

    int hash(int key) { return key % capacity; }

    void insert(int key) {
        int index = hash(key);
        while (table[index] != -1)
            index = (index + 1) % capacity;
        table[index] = key;
    }
};

