class ChainedHash {
    std::vector<std::list<int>> table;
public:
    ChainedHash(int size) : table(size) {}

    void insert(int key) {
        int index = key % table.size();
        table[index].push_back(key);
    }

    bool search(int key) {
        int index = key % table.size();
        for (auto& k : table[index])
            if (k == key) return true;
        return false;
    }
};

